#!/bin/bash echo "🚀 Deploying Read2Rich to production..." echo "" # Check if .env file exists if [ ! -f .env ]; then echo "❌ .env file not found. Please create one from .env.example" exit 1 fi echo "✅ Environment configuration found" echo "" # Build and start all services echo "🐳 Building and starting services..." docker-compose -f docker-compose.prod.yml up -d --build echo "" # Wait for services to be ready echo "⏳ Waiting for services to be ready..." sleep 30 echo "" # Check service status echo "📊 Service status:" docker-compose -f docker-compose.prod.yml ps echo "" echo "✅ Deployment completed!" echo "Frontend: http://localhost:3000" echo "Backend API: http://localhost:8000" echo "API Docs: http://localhost:8000/docs"
