#!/bin/bash echo "🚀 Initializing Read2Rich project..." echo "" # Create necessary directories echo "📁 Creating project structure..." mkdir -p frontend/src/{components,pages,hooks,utils,types,locales/{en,ko,ja},assets/{images,icons,styles}} mkdir -p backend/app/{api/v1,core,models,services,utils,schemas} mkdir -p microservices/{ingestion,processing,embedding,scoring,api_gateway} mkdir -p docker/{nginx,postgres} mkdir -p scripts docs echo "✅ Project structure created" echo "" # Copy environment file if [ ! -f .env ]; then echo "📝 Creating .env file from template..." cp .env.example .env echo "⚠️  Please update .env file with your actual configuration" else echo "✅ .env file already exists" fi echo "" # Initialize Git repository if [ ! -d .git ]; then echo "🔧 Initializing Git repository..." git init git add . git commit -m "Initial commit: Read2Rich project setup" echo "✅ Git repository initialized" else echo "✅ Git repository already exists" fi echo "" echo "🎉 Project initialization completed!" echo "" echo "📋 Next steps:" echo "  1. Update .env file with your configuration" echo "  2. Run: ./scripts/start-dev.sh" echo "  3. Visit: http://localhost:3000" echo "" echo "📚 Documentation:" echo "  - README.md: Project overview" echo "  - OAUTH_SETUP_GUIDE.md: OAuth configuration" echo "  - docs/: Detailed documentation"
