#!/bin/bash echo "🚀 Starting Read2Rich development environment..." echo "" echo "📋 Prerequisites:" echo "  - Docker & Docker Compose installed" echo "  - Node.js 18+ installed" echo "  - Python 3.11+ installed" echo "" # Check if Docker is running if ! docker info > /dev/null 2>&1; then echo "❌ Docker is not running. Please start Docker and try again." exit 1 fi echo "✅ Docker is running" echo "" # Start backend services echo "🐳 Starting backend services..." docker-compose up -d postgres redis kafka zookeeper qdrant echo "✅ Backend services started" echo "" # Wait for services to be ready echo "⏳ Waiting for services to be ready..." sleep 10 echo "" # Install frontend dependencies echo "📦 Installing frontend dependencies..." cd frontend npm install echo "✅ Frontend dependencies installed" echo "" # Start frontend development server echo "🌐 Starting frontend development server..." echo "Frontend will be available at: http://localhost:3000" npm run dev
